import firebase from 'firebase/app';
import 'firebase/firestore';
import 'firebase/auth';
import 'firebase/analytics';

import { useAuthState } from 'react-firebase-hooks/auth';
import { useCollectionData } from 'react-firebase-hooks/firestore';
import { useRef, useState } from 'react';

import './AzChatbot.scss';
import {FaAngleDown} from 'react-icons/fa';

firebase.initializeApp({
  apiKey: "AIzaSyCgtoweQnHcYa0a0B9dLwFbE9KTGVCg6_g",
  authDomain: "chatbot-896c0.firebaseapp.com",
  projectId: "chatbot-896c0",
  storageBucket: "chatbot-896c0.appspot.com",
  messagingSenderId: "368946359439",
  appId: "1:368946359439:web:0aec4eed96e92447d8d4fa",
  measurementId: "G-NYYF52TG6M"
});

const auth = firebase.auth();
const firestore = firebase.firestore();

function AzChatbot() {

  const [user] = useAuthState(auth);
  return (
    <div className="AZChat">    
        <div className={"chatContainer"}>
          <div className={"chatHeader"}>
            <h5>⚛️🔥💬</h5>
            <SignOut />
          </div>
          <button className={"btnStyle"} 
            onClick={() => {
            if(tg == false){
            document.querySelector(".AZChat .chatContainer .chatRoom #mainChat").style.height = "0rem";
            document.querySelector(".AZChat .chatContainer .chatRoom #mainChat").style.maxHeight = "0rem";
            document.querySelector(".AZChat .chatContainer .chatRoom #mainChat").style.padding = "0";
            document.querySelector(".AZChat .chatContainer .chatHeader").style.bottom = "2rem"; 
            document.querySelector(".AZChat .chatContainer .chatHeader").style.top = "auto"; 
            tg = true;
            }else if(tg == true){
                document.querySelector(".AZChat .chatContainer .chatRoom #mainChat").style.height = "20rem";
                document.querySelector(".AZChat .chatContainer .chatRoom #mainChat").style.maxHeight = "20rem";
                document.querySelector(".AZChat .chatContainer .chatRoom #mainChat").style.padding = "10px 0px";
                document.querySelector(".AZChat .chatContainer .chatHeader").style.bottom = "auto"; 
                document.querySelector(".AZChat .chatContainer .chatHeader").style.top = "5.5rem"; 
                tg = false;
            }
            }}><FaAngleDown />
        </button>
          <div className={"chatRoom"}>
            {user ? <ChatRoom /> : <SignIn />}
          </div>         
        </div>
    </div>
  );
}

function SignIn(){

  const signInWithGoogle = () => {
    const provider = new firebase.auth.GoogleAuthProvider();
    auth.signInWithPopup(provider);
  }

  return(
    <button onClick={signInWithGoogle}>Sign in with Google</button>
  )
}

function SignOut(){
  return auth.currentUser && (
    <button onClick={() => auth.signOut()}>Sign Out</button>
  )
}

function ChatRoom(){
  const dummy = useRef();
  const messagesRef = firestore.collection('messages');
  const query = messagesRef.orderBy('createdAt').limit(25);
  const [messages] = useCollectionData(query, {idField: 'id'});
  const [formValue, setFormValue] = useState('');
  const sendMessage = async(e) => {
    e.preventDefault();

    const { uid, photoURL } = auth.currentUser;

    await messagesRef.add({
      text: formValue,
      createdAt: firebase.firestore.FieldValue.serverTimestamp(),
      uid,
      photoURL
    });

    setFormValue('');

    dummy.current.scrollIntoView({ behavior: 'smooth' });
  }

  return(
    <>
      <section id="mainChat">
        {messages && messages.map(msg => <ChatMessage key={msg.id} message={msg} />)}
        <div ref={dummy}></div>
      </section>

      <form onSubmit={sendMessage}>
        <input value={formValue} onChange={(e) => setFormValue(e.target.value)} placeholder="say something nice" />
        <button type="submit" disabled={!formValue}>🕊️</button>
      </form>
    </>
  )
}

function ChatMessage(props){
  const {text, uid, photoURL} = props.message;

  const messageClass = uid === auth.currentUser.uid ? 'sent' : 'received';
  
  return(
    <div className={`message ${messageClass}`}>
      <img src={photoURL} />
      <p>{text}</p>
    </div>
  )
}

export default AzChatbot;
