import React from 'react';

class Plugin extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    // Home - to top button
    btnClkd() {window.scrollTo(0,0);}

    // Services - toggle colors
    toggler = undefined;
    colorToggle(Element, Class1, Class2) {
        Element = document.getElementsByClassName("targetCard")[0]; Class1 = "cl1"; Class2 = "cl2";        
        
        if((Element.classList.contains(`${Class1}`) === true) && (toggler === false)){
            Element.classList.remove(`${Class1}`);  
            Element.classList.add(`${Class2}`);  
            toggler = true;
        }; 
        
        if((Element.classList.contains(`${Class1}`) === false) && (Element.classList.contains(`${Class2}`) === false)){
            Element.classList.add(`${Class1}`);  
            toggler = false;
        }; 

        if((Element.classList.contains(`${Class2}`) === true) && (toggler === true)){
            Element.classList.remove(`${Class2}`);  
            Element.classList.add(`${Class1}`);
        };
        
    }
    render() {
        return (
            <div>
                
            </div>
        );
    }
}

export default Plugin;
