export default function formValidate(values) {

    let errors = {};

    if( !values.username.trim() ){
        errors.username = "Username required"
    }

    if (!values.useremail) {
        errors.useremail = 'Email required';
    } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.useremail)) {
        errors.useremail = 'Email address is invalid';
    }

    if (!values.userpass) {
        errors.userpass = 'Password is required';
    } else if (values.userpass.length < 6) {
    errors.userpass = 'Password needs to be 6 characters or more';
    }

    if (!values.userpassconf) {
    errors.userpassconf = 'Password is required';
    } else if (values.userpassconf !== values.userpass) {
    errors.userpassconf = 'Passwords do not match';
    }

    return errors;
}
