import React,{useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Accordion, Card, Button} from 'react-bootstrap';
import CardsContentJSON1 from './sCards.json';
const jsonArr1 = JSON.stringify(CardsContentJSON1.CardsContent);

    //Today

class clServices extends React.Component {
    constructor(props) {
        super(props);
        this.state = { message: "accordion close" };        
    }
    accordionActive(){
        this.setState({
            message: "accordion open"
        });
    }
    
    render() {        
        //let jsnCntNum = parseInt(this.props.cntNum) + 2;
        return (
            <Card>
                <Card.Header>
                    <Accordion.Toggle as={Button} variant="link" eventKey={this.props.keyNum} onClick={() => this.accordionActive()}>
                        {jsonArr1.split('"')[`${this.props.cntNum}`]} - {this.state.message}
                    </Accordion.Toggle>
                </Card.Header>
                <Accordion.Collapse eventKey={this.props.keyNum}>
                    <Card.Body>{jsonArr1.split('"')[`${parseInt(this.props.cntNum) + 2}`]}</Card.Body>
                </Accordion.Collapse>
            </Card>
        );
    }
}


export default clServices;
