import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../Styles/style0.css';
import {Accordion, Card, Button} from 'react-bootstrap';
import CardsContentJSON from './sCards.json';
import ClServices from './clServices';
import {FaPaintBrush} from 'react-icons/fa';
const jsonArr = JSON.stringify(CardsContentJSON.CardsContent);
const services = () => {
    //console.log(jsonArr.slice(jsonArr.search('"',0)+1,5));
    //console.log(jsonArr.split('"')[1] , jsonArr.split('"')[3] +"\n"+ jsonArr.split('"')[5] , jsonArr.split('"')[7]);    
    //console.log(jsonArr);
    const colorToggle = (Element, Class1, Class2) => {
        var toggler = undefined;        
        const tg0 = () => {
        if((Element.classList.contains(`${Class1}`) === false) && (Element.classList.contains(`${Class2}`) === false)){
            Element.classList.add(`${Class1}`);  
            toggler = false;
        }};  
        const tg1 = () => {
        if((Element.classList.contains(`${Class1}`) === true) && (toggler === false)){
            Element.classList.remove(`${Class1}`);  
            Element.classList.add(`${Class2}`);  
            toggler = true;
        }}; 
        const tg2 = () => {
        if((Element.classList.contains(`${Class2}`) === true) && (toggler === true)){
            Element.classList.remove(`${Class2}`);  
            Element.classList.add(`${Class1}`);
        }};
    }
    return (        
    <>
        <h1 className="text-center">Services 
            <FaPaintBrush onClick={() => colorToggle(
                document.getElementsByClassName("targetCard")[0],"cl1","cl2"
            )}></FaPaintBrush>
        </h1>
        <Accordion defaultActiveKey="3">
            <Card>
                <Card.Header>
                    <Accordion.Toggle as={Button} variant="link" eventKey="0">
                        {jsonArr.split('"')[1]}                        
                    </Accordion.Toggle>
                </Card.Header>
                <Accordion.Collapse eventKey="0">                        
                    <Card.Body>{jsonArr.split('"')[3]}</Card.Body> 
                </Accordion.Collapse>
            </Card>
            <Card>
                <Accordion.Toggle as={Card.Header} className={`btn btn-link text-left pl-4`} variant="link" eventKey="1">
                    {jsonArr.split('"')[5]}
                </Accordion.Toggle>
                <Accordion.Collapse eventKey="1">
                    <Card.Body>{jsonArr.split('"')[7]}</Card.Body>
                </Accordion.Collapse>
            </Card>

            {/*Today*/}
            <ClServices keyNum="2" cntNum="9"/>
            <ClServices keyNum="3" cntNum="13" customClass={"targetCard"}></ClServices>
            <ClServices keyNum="4" cntNum="17"/>
        </Accordion>
    </>
    );
};

export default services;
