import React from 'react';

const Counter = (props) => {
    // constructor(props){
    //     super(props);
    //     this.state = {countNumber: 99}
    // }

    // clickHandlerUp = () => this.setState({ countNumber: this.state.countNumber+=1 });
    // clickHandlerDown = () => this.setState({ countNumber: this.state.countNumber-=1 });
    
    return (
        <>
            <h1>{props.countNumber}</h1>
            <button onClick={props.countNumber+=1}>Up</button>
            <button onClick={() => props.countNumber-=1}>Down</button>
        </>
    )

}

export default Counter;
