import React, { useState } from 'react';

const Counter = (props) => {
    // constructor(props){
    //     super(props);
    //     this.state = {countNumber: 99}
    // }

    // clickHandlerUp = () => this.setState({ countNumber: this.state.countNumber+=1 });
    // clickHandlerDown = () => this.setState({ countNumber: this.state.countNumber-=1 });
    const [count, setcount] = useState(props.countNumber),
          countup = () => setcount(count+=1),
          countdown = () => setcount(count-=1);
    return (
        <>
            <h1>{count}</h1>
            <button onClick={countup}>Up</button>
            <button onClick={countdown}>Down</button>
        </>
    )

}

export default Counter;
