import React, { Component } from 'react';
import PropTypes from 'prop-types';
import ChatBot from 'react-simple-chatbot';

class Reviewc extends Component {
  constructor(props) {
    super(props);

    this.state = {
      name: 'Ahmed',
      city: 'Alexandria',
      number: '01090761007',
      site: 'Has health',
      slogane: 'where care found',
    };
  }

  componentWillMount() {
    const { steps } = this.props;
    //const { name, city, number, site, slogane } = steps;

    //this.setState({ name, city, number, site, slogane });
    const { name } = steps;
    this.setState({ name });
  }

  render() {
    const  { name, city, number, site, slogane } = this.state;
    return (
      <div style={{ width: '100%' }}>
        <h3>Summary</h3>
        <table>
          <tbody>
            <tr>
              <td>Name</td>
              <td>{name.value}</td>
            </tr>
            <tr>
              <td>City</td>
              <td>{city}</td>
            </tr>
            <tr>
              <td>Number</td>
              <td>{number}</td>
            </tr>
            <tr>
              <td>Site Name</td>
              <td>{site}</td>
            </tr>
            <tr>
              <td>Site Slogane</td>
              <td>{slogane}</td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  }
}

Reviewc.propTypes = {
  steps: PropTypes.object,
};

Reviewc.defaultProps = {
  steps: undefined,
};
let ans1 = "";
class ChatBotExp2 extends Component {
    render() {
      return (
        <ChatBot
        steps={[
            {
              id: '1',
              message: 'Hi ahmed what do you mind?',
              trigger: '2',
            },
            {
              id: '2',
              user: true,
              trigger: '3',
            },
            {
              id: '3',
              validator: (value) => {
                if ( value == "what city?" ) {
                  ans1 = 'Alexandria';
                } 
                return true;
              },
              message: ans1,
            }
          ]}
        />
      );
    }
  }
  
export default ChatBotExp2;
