import React, { useEffect } from 'react'
import {auth} from '../Firebase/Firebase.js';

function SignOut(){

  useEffect(() => {
    document.querySelector(".AZChat .chatContainer .chatHeader").style.bottom = "2rem"; 
    document.querySelector(".AZChat .chatContainer .chatHeader").style.top = "auto"; 
    document.querySelector(".AZChat .chatContainer .chatRoom").style.height = "calc(100% - 2.3rem)";
    // document.querySelector(".AZChat .chatContainer .chatRoom").style.backgroundColor = "#ffffff";
    // if( document.querySelector(".AZChat .chatContainer .chatHeader").style.top === "0rem" 
    //     && document.querySelector("#mainChat") === null
    // ){
    //   document.querySelector(".AZChat .chatContainer .chatRoom").style.backgroundColor = "#efe9f4";
    // }else{
    //   document.querySelector(".AZChat .chatContainer .chatRoom").style.backgroundColor = "#ffffff";
    // }
  }, []);

    return auth.currentUser && (
      <button onClick={() => 
        {auth.signOut();
          // document.querySelector(".AZChat .chatContainer .chatRoom").style.backgroundColor = "#efe9f4";  
        }
      }>Sign Out</button>
    )
}

export default SignOut
