    // }

    clickHandlerUp = () => this.setState({ countNumber: this.state.countNumber+=1 });
    clickHandlerDown = () => this.setState({ countNumber: this.state.countNumber-=1 });
    
    render() {
        return (
            <>
                {/* <h1>{this.state.countNumber}</h1>
                <button onClick={() => this.clickHandlerUp()}>Up</button>
                <button onClick={() => this.clickHandlerDown()}>Down</button> */}
                <Counter countNumber="99"/>
            </>
        );
    }
}

export default CounterConainer;
