import { useState, useEffect } from 'react';

const useForm = () => {
  const [values, setValues] = useState({
    username: '',
    useremail: '',
    userpass: '',
    userpassconf: ''
  });

  const [errors, setErrors] = useState({});  

  const handleChange = e => {
    const { name, value } = e.target;
    setValues({
      ...values,
      [name]: value
    });
  };
  return (handleChange);
}

export default useForm;
