import React, { Component } from 'react';
import PropTypes from 'prop-types';
import ChatBot from 'react-simple-chatbot';
const ctvl = "Alexa"
class Reviewc extends Component {
  constructor(props) {
    super(props);

    this.state = {
      name: 'ass',
      city: 'alexa',
      number: '05456',
      site: 'fr',
      slogane: 'jo',
    };
  }

  componentWillMount() {
    //const { steps } = this.props;
    //const { name, city, number, site, slogane } = steps;

    //this.setState({ name, city, number, site, slogane });
  }

  render() {
    const  { name, city, number, site, slogane } = this.props.children;
    return (
      <div style={{ width: '100%' }}>
        <h3>Summary</h3>
        <table>
          <tbody>
            <tr>
              <td>Name</td>
              <td>{name.value}</td>
            </tr>
            <tr>
              <td>City</td>
              <td>{city.value}</td>
            </tr>
            <tr>
              <td>Number</td>
              <td>{number.value}</td>
            </tr>
            <tr>
              <td>Site Name</td>
              <td>{site.value}</td>
            </tr>
            <tr>
              <td>Site Slogane</td>
              <td>{slogane.value}</td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  }
}

Reviewc.propTypes = {
  steps: PropTypes.object,
};

Reviewc.defaultProps = {
  steps: undefined,
};

class HasChatBoot extends Component {
  render() {
    return (
      <ChatBot
        steps={[
            /*
          {
            id: '1',
            message: 'What is your name?',
            trigger: 'name',
          },
          {
            id: 'name',
            user: true,
            trigger: '3',
          },

          {
            id: '3',
            message: 'Hi {previousValue}! Can i help you?',
            trigger: '4',
          },

          {
            id: '4',
            options: [  
                { value: 'city', label: 'City', trigger: 'get-city' },
                { value: 'number', label: 'Number', trigger: 'get-number' },
                { value: 'site', label: 'Site name', trigger: 'get-snm' },
                { value: 'slogane', label: 'Site slogane', trigger: 'get-ssl' },                
            ],
          },
          {id: 'get-city',    message: 'Wooow Alexandria',    trigger: '6',},
          {id: 'get-number',    message: 'ph: 01090761007',    trigger: '6',},
          {id: 'get-snm',    message: 'Welcome has health',    trigger: '6',},
          {id: 'get-ssl',    message: 'Good: where care found',    trigger: '6',},
        */




          {
            id: '6',
            message: 'Great! Check out your summary',
            trigger: 'review',
          },
          {
            id: 'review',
            component: <Reviewc/>,
            asMessage: true,
            trigger: 'update',
          },
          {
            id: 'update',
            message: 'Would you like to update some field?',
            trigger: 'update-question',
          },
          {
            id: 'update-question',
            options: [
              { value: 'yes', label: 'Yes', trigger: 'update-yes' },
              { value: 'no', label: 'No', trigger: 'end-message' },
            ],
          },
          {
            id: 'update-yes',
            message: 'What field would you like to update?',
            trigger: 'update-fields',
          },
          {
            id: 'update-fields',
            options: [  
              { value: 'name', label: 'Name', trigger: 'update-name' },
              { value: 'city', label: 'City', trigger: 'update-city' },
              { value: 'number', label: 'Number', trigger: 'update-number' },
              { value: 'site', label: 'Site Name', trigger: 'update-sName' },
              { value: 'slogane', label: 'Site Slogane', trigger: 'update-sSlogane' },
            ],
          },
          {
            id: 'update-name',
            update: 'name',
            trigger: '6',
          },
          {
            id: 'update-city',
            update: 'city',
            trigger: '6',
          },
          {
            id: 'update-number',
            update: 'number',
            trigger: '6',
          },
          {
            id: 'update-sName',
            update: 'site',
            trigger: '6',
          },
          {
            id: 'update-sSlogane',
            update: 'slogane',
            trigger: '6',
          },
          {
            id: 'end-message',
            message: 'Thanks! Your data was submitted successfully!',
            end: true,
          },
        ]}
      />
    );
  }
}

export default HasChatBoot;
